mapper.select:
SELECT c.id, c.name, c.sido_name, c.sido_code, c.gugun_name, c.gugun_code, c.detail_address, c.latitude, c.longitude, c.image_url, c.introduction, c.telephone, c.homepage_url, COUNT(r.id) AS review_cnt FROM camping c LEFT JOIN review r ON c.id = r.camping_id WHERE image_url IS NOT NULL AND image_url != ''
AND (name LIKE CONCAT('%', #{keyword}, '%') OR detail_address LIKE CONCAT('%', #{keyword}, '%') OR introduction LIKE CONCAT('%', #{keyword}, '%'))
AND sido_code = #{sidoCode}
AND gugun_code = #{gugunCode}
GROUP BY c.id
ORDER BY ${pagingAndSorting.order} ${pagingAndSorting.sort}
ORDER BY id ASC
LIMIT #{pagingAndSorting.pageCnt} OFFSET #{pagingAndSorting.offset}

SELECT COUNT(*) FROM camping WHERE image_url IS NOT NULL AND image_url != ''
AND (name LIKE CONCAT('%', #{keyword}, '%') OR detail_address LIKE CONCAT('%', #{keyword}, '%') OR introduction LIKE CONCAT('%', #{keyword}, '%'))
AND sido_code = #{sidoCode}
AND gugun_code = #{gugunCode}

SELECT c.id, c.name, c.sido_name, c.sido_code, c.gugun_name, c.gugun_code, c.detail_address, c.latitude, c.longitude, c.image_url, c.introduction, c.telephone, c.homepage_url, CAST((6371 * ACOS(COS(RADIANS(a.latitude)) * COS(RADIANS(c.latitude)) * COS(RADIANS(c.longitude) - RADIANS(a.longitude)) + SIN(RADIANS(a.latitude)) * SIN(RADIANS(c.latitude)))) AS DOUBLE) AS distance, COUNT(r.id) AS review_cnt FROM camping c JOIN attractions a ON a.no = #{attractionId} LEFT JOIN review r ON r.camping_id = c.id WHERE a.area_code = c.sido_code AND a.si_gun_gu_code = c.gugun_code AND image_url IS NOT NULL AND image_url != '' GROUP BY c.id
ORDER BY ${pagingAndSorting.order} ${pagingAndSorting.sort}
ORDER BY distance ASC
LIMIT #{pagingAndSorting.pageCnt} OFFSET #{pagingAndSorting.offset}
